package Java;

public class Operater {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=2;
		int c=a+b;
		System.out.println("add the values of c"+" "+c);
		int d=a-b;
		System.out.println("sub the values of d"+" "+d);
		int e=a*b;
		System.out.println("Mul the values of e"+" "+e);
		int f=a/b;
		System.out.println("div the values of f"+" "+f);
		int g=a%b;
		System.out.println("mod the values of g"+" "+g);
		
		

	}

}
