package Java;

public class assign_operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//create variables
		int a=5;
		int var;
		//assign value using =
		var=a;
		System.out.println("var using =:"+var);
		//assign value using =+
		
		var += a; // var =var +a
		System.out.println("var using +=:"+var);
		//assign value using =*
		
		var *=a; // var =var*a
		System.out.println("var using *=:"+var);
		//assign value using =-
		
		var -=a; //var =var -a
		System.out.println("var using -=:"+var);
		

	}

}
