package Java;

public class controlStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5 ,y=12;
		if(x+y>20)
		{
			System.out.println("The value of x & y is greater than 20");
		}
		else {
			System.out.println("The value is lesser than 20");
		}
	}

}
