package Java;

public class ifelseif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=12, c=18;
		if(a>b && a>c)
		{
			System.out.println("a is greater");
		}
		else if(b>a && b>c) {
			System.out.println("b is greater");
			
		}
		else {
			System.out.println("c is greater");
		}

	}

}
